import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

interface User {
  id: string;
  email: string;
  username: string;
}
import { 
  Bot, 
  Plus, 
  MessageSquare, 
  User as UserIcon, 
  CreditCard, 
  LogOut, 
  Trash2,
  Settings
} from "lucide-react";
import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuItem,
  SidebarMenuButton,
  SidebarHeader,
  SidebarFooter,
  SidebarTrigger,
  useSidebar,
} from "@/components/ui/sidebar";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";

import { useToast } from "@/hooks/use-toast";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { 
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

interface ChatSession {
  id: string;
  title: string;
  created_at: string;
  updated_at: string;
}

interface ChatSidebarProps {
  user: User;
  currentChatId: string | null;
  onChatSelect: (chatId: string) => void;
  onCreateChat: (title?: string) => Promise<string | null>;
}

export function ChatSidebar({ 
  user, 
  currentChatId, 
  onChatSelect, 
  onCreateChat 
}: ChatSidebarProps) {
  const [chatSessions, setChatSessions] = useState<ChatSession[]>([]);
  const [loading, setLoading] = useState(true);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [chatToDelete, setChatToDelete] = useState<string | null>(null);
  const { state } = useSidebar();
  const collapsed = state === "collapsed";
  const navigate = useNavigate();
  const { toast } = useToast();

  const fetchChatSessions = async () => {
    try {
      const token = localStorage.getItem('auth_token');
      if (!token) return;

      const response = await fetch('/api/chat-sessions', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setChatSessions(data || []);
      }
    } catch (error) {
      console.error("Error fetching chat sessions:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchChatSessions();
  }, []);

  const handleNewChat = async () => {
    const chatId = await onCreateChat("Nueva conversaci√≥n");
    if (chatId) {
      await fetchChatSessions();
    }
  };

  const handleDeleteChat = async (chatId: string) => {
    try {
      const token = localStorage.getItem('auth_token');
      if (!token) return;

      const response = await fetch(`/api/chat-sessions/${chatId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        toast({
          variant: "destructive",
          title: "Error",
          description: "No se pudo eliminar la conversaci√≥n.",
        });
        return;
      }

      setChatSessions(prev => prev.filter(chat => chat.id !== chatId));

      if (currentChatId === chatId) {
        onChatSelect("");
      }

      toast({
        title: "Conversaci√≥n eliminada",
        description: "La conversaci√≥n ha sido eliminada correctamente.",
      });
    } catch (error) {
      console.error("Error deleting chat:", error);
      toast({
        variant: "destructive",
        title: "Error",
        description: "No se pudo eliminar la conversaci√≥n.",
      });
    }
  };

  const handleLogout = async () => {
    try {
      localStorage.removeItem('auth_token');
      navigate("/");
    } catch (error) {
      console.error("Error signing out:", error);
    }
  };

  const getUserInitials = () => {
    if (user.user_metadata?.username) {
      return user.user_metadata.username.slice(0, 2).toUpperCase();
    }
    if (user.email) {
      return user.email.slice(0, 2).toUpperCase();
    }
    return "US";
  };

  return (
    <>
      <Sidebar className={collapsed ? "w-14" : "w-64"} collapsible="icon">
        <SidebarHeader className="p-4">
          <div className="flex items-center gap-3">
            <div className="p-2 bg-primary/10 rounded-lg">
              <Bot className="h-6 w-6 text-primary" />
            </div>
            {!collapsed && (
              <div>
                <h1 className="font-bold text-lg">Precensus AI</h1>
                <p className="text-sm text-muted-foreground">
                  Automatizaci√≥n Inteligente
                </p>
              </div>
            )}
          </div>
          <SidebarTrigger className="ml-auto" />
        </SidebarHeader>

        <SidebarContent className="flex-1">
          <SidebarGroup>
            <SidebarGroupLabel className="flex items-center justify-between">
              {!collapsed && "Conversaciones"}
              <Button
                variant="ghost"
                size="sm"
                onClick={handleNewChat}
                className="h-6 w-6 p-0"
                title="Nueva conversaci√≥n"
              >
                <Plus className="h-4 w-4" />
              </Button>
            </SidebarGroupLabel>

            <SidebarGroupContent>
              <SidebarMenu>
                {loading ? (
                  <div className="p-4 text-center">
                    <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-primary mx-auto"></div>
                  </div>
                ) : chatSessions.length === 0 ? (
                  <div className="p-4 text-center text-muted-foreground">
                    {!collapsed && "No hay conversaciones"}
                  </div>
                ) : (
                  chatSessions.map((chat) => (
                    <SidebarMenuItem key={chat.id}>
                      <SidebarMenuButton
                        onClick={() => onChatSelect(chat.id)}
                        isActive={currentChatId === chat.id}
                        className="flex items-center justify-between group"
                      >
                        <div className="flex items-center gap-2 min-w-0 flex-1">
                          <MessageSquare className="h-4 w-4 flex-shrink-0" />
                          {!collapsed && (
                            <span className="truncate">{chat.title}</span>
                          )}
                        </div>
                        {!collapsed && (
                          <Button
                            variant="ghost"
                            size="sm"
                            className="h-auto w-auto p-1 opacity-0 group-hover:opacity-100"
                            onClick={(e) => {
                              e.stopPropagation();
                              setChatToDelete(chat.id);
                              setDeleteDialogOpen(true);
                            }}
                          >
                            <Trash2 className="h-3 w-3" />
                          </Button>
                        )}
                      </SidebarMenuButton>
                    </SidebarMenuItem>
                  ))
                )}
              </SidebarMenu>
            </SidebarGroupContent>
          </SidebarGroup>
        </SidebarContent>

        <SidebarFooter className="p-4">
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" className="w-full justify-start">
                <Avatar className="h-8 w-8 mr-3">
                  <AvatarFallback className="text-xs">
                    {getUserInitials()}
                  </AvatarFallback>
                </Avatar>
                {!collapsed && (
                  <div className="flex-1 text-left">
                    <p className="text-sm font-medium">
                      {user.user_metadata?.username || user.email?.split("@")[0]}
                    </p>
                    <p className="text-xs text-muted-foreground">
                      {user.email}
                    </p>
                  </div>
                )}
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end" className="w-56">
              <DropdownMenuItem onClick={() => navigate("/")}>
                <UserIcon className="mr-2 h-4 w-4" />
                <span>Mi cuenta</span>
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => navigate("/#precios")}>
                <CreditCard className="mr-2 h-4 w-4" />
                <span>Precios</span>
              </DropdownMenuItem>
              <DropdownMenuItem>
                <Settings className="mr-2 h-4 w-4" />
                <span>Configuraci√≥n</span>
              </DropdownMenuItem>
              <DropdownMenuSeparator />
              <DropdownMenuItem onClick={handleLogout}>
                <LogOut className="mr-2 h-4 w-4" />
                <span>Cerrar sesi√≥n</span>
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </SidebarFooter>
      </Sidebar>

      <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>¬øEliminar conversaci√≥n?</AlertDialogTitle>
            <AlertDialogDescription>
              Esta acci√≥n no se puede deshacer. Se eliminar√° permanentemente la conversaci√≥n y todos sus mensajes.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancelar</AlertDialogCancel>
            <AlertDialogAction
              onClick={() => {
                if (chatToDelete) {
                  handleDeleteChat(chatToDelete);
                  setChatToDelete(null);
                }
                setDeleteDialogOpen(false);
              }}
              className="bg-destructive text-destructive-foreground"
            >
              Eliminar
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </>
  );
}

client/src/components/HeroChat.tsx.content:
import { useState, useRef, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Send, Bot, User } from 'lucide-react';
import { supabase } from '@/integrations/supabase/client';

interface Message {
  id: string;
  text: string;
  isBot: boolean;
  timestamp: Date;
}

export const HeroChat = () => {
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      text: '¬øNecesitas informaci√≥n? Preg√∫ntame lo que quieras üí¨',
      isBot: true,
      timestamp: new Date()
    }
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const detectAutomationKeywords = (message: string) => {
    const keywords = [
      'crear agente', 'creame', 'automatizacion', 'automatizaci√≥n', 
      'agente', 'bot', 'crear bot', 'workflow', 'proceso automatico',
      'automatizar', 'generar agente', 'hacer agente'
    ];

    const lowerMessage = message.toLowerCase();
    return keywords.some(keyword => lowerMessage.includes(keyword));
  };

  const createAutomation = async () => {
    if (!inputMessage.trim()) return;

    setIsLoading(true);
    try {
      const { data, error } = await supabase.functions.invoke('orchestrate', {
        body: {
          prompt: inputMessage,
          dry_run: false
        }
      });

      if (error) throw error;

      const botMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: `üéâ ¬°Automatizaci√≥n creada exitosamente!

Workflow ID: ${data.workflow_id}

üìä **Resumen de ejecuci√≥n:**
${data.execution_summary.message}

ü§ñ **Modelos utilizados:** ${data.models_used.join(', ')}

El workflow ha sido guardado y est√° listo para usar.`,
        isBot: true,
        timestamp: new Date()
      };

      setMessages(prev => [...prev, botMessage]);
      setInputMessage('');
    } catch (error) {
      console.error('Error creating automation:', error);
      const errorMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: 'Lo siento, hubo un error al crear la automatizaci√≥n. Por favor, intenta de nuevo.',
        isBot: true,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const sendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputMessage,
      isBot: false,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);

    // Check if user wants to create automation
    if (detectAutomationKeywords(inputMessage)) {
      const suggestionMessage: Message = {
        id: (Date.now() + 0.5).toString(),
        text: 'ü§ñ He detectado que quieres crear una automatizaci√≥n. Te recomiendo usar el bot√≥n "Crear Automatizaci√≥n" para un proceso m√°s completo y eficiente.',
        isBot: true,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, suggestionMessage]);
      setInputMessage('');
      return;
    }

    setInputMessage('');
    setIsLoading(true);

    try {
      const { data, error } = await supabase.functions.invoke('chat-ai', {
        body: {
          message: inputMessage,
          sessionId: 'landing-page-chat'
        }
      });

      if (error) throw error;

      const botMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: data.response || 'Lo siento, hubo un error al procesar tu mensaje.',
        isBot: true,
        timestamp: new Date()
      };

      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: 'Lo siento, no pude procesar tu mensaje en este momento. Por favor, intenta de nuevo.',
        isBot: true,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="w-full max-w-2xl mx-auto mt-6 relative">
      <Card className="border border-border/50 shadow-md rounded-3xl overflow-hidden flex flex-col h-[500px]">
        <CardContent className="p-4 flex flex-col h-full relative">
          <div className="flex-1 overflow-y-auto space-y-3 p-3 bg-muted/20 rounded-2xl mb-32">
            {messages.map((message) => (
              <div
                key={message.id}
                className={`flex gap-2 ${message.isBot ? 'justify-start' : 'justify-end'}`}
              >
                {message.isBot && (
                  <div className="w-6 h-6 rounded-full bg-primary/10 flex items-center justify-center flex-shrink-0 mt-1">
                    <Bot className="h-3 w-3 text-primary" />
                  </div>
                )}
                <div
                  className={`max-w-[75%] px-3 py-2 rounded-2xl text-sm ${
                    message.isBot
                      ? 'bg-background border text-foreground'
                      : 'bg-primary text-primary-foreground'
                  }`}
                >
                  {message.text}
                </div>
                {!message.isBot && (
                  <div className="w-6 h-6 rounded-full bg-primary flex items-center justify-center flex-shrink-0 mt-1">
                    <User className="h-3 w-3 text-primary-foreground" />
                  </div>
                )}
              </div>
            ))}
            {isLoading && (
              <div className="flex gap-2 justify-start">
                <div className="w-6 h-6 rounded-full bg-primary/10 flex items-center justify-center flex-shrink-0 mt-1">
                  <Bot className="h-3 w-3 text-primary" />
                </div>
                <div className="bg-background border px-3 py-2 rounded-2xl text-sm">
                  <div className="flex gap-1">
                    <div className="w-1.5 h-1.5 bg-muted-foreground rounded-full animate-bounce"></div>
                    <div className="w-1.5 h-1.5 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                    <div className="w-1.5 h-1.5 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                  </div>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Fixed input area at absolute bottom */}
          <div className="absolute bottom-0 left-0 right-0 bg-background/98 backdrop-blur-md border-t p-4 rounded-b-3xl">
            <div className="flex gap-2 max-w-full mx-auto mb-3">
              <Button
                onClick={createAutomation}
                disabled={isLoading || !inputMessage.trim()}
                size="sm"
                variant="outline"
                className="px-3 rounded-full border-primary text-primary hover:bg-primary hover:text-primary-foreground"
              >
                ü§ñ Crear Automatizaci√≥n
              </Button>
            </div>
            <div className="flex gap-2 max-w-full mx-auto">
              <Input
                placeholder="Escribe tu pregunta o describe la automatizaci√≥n que necesitas..."
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                disabled={isLoading}
                className="flex-1 rounded-full border-border/50"
              />
              <Button
                onClick={sendMessage}
                disabled={isLoading || !inputMessage.trim()}
                size="sm"
                className="px-3 rounded-full"
              >
                <Send className="h-3 w-3" />
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

